                <div class="accordion-item">
                    <h2 class="accordion-header" id="headingNine">
                        <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapseNine" aria-expanded="false" aria-controls="collapseNine">
                            <h3>Dayak</h3>
                        </button>
                    </h2>
                    <div id="collapseNine" class="accordion-collapse collapse" aria-labelledby="headingNine" data-bs-parent="#accordionMap">
                        <div class="accordion-body">
                            <div class="mapouter">
                                <div class="gmap_canvas">
                                    <iframe width="80%" height="400px" id="gmap_canvas" src="https://maps.google.com/maps?q=Kalimantan&t=&z=6&ie=UTF8&iwloc=&output=embed" frameborder="0" marginheight="0" marginwidth="0"></iframe>
                                </div>
