INSERT INTO `dictionary` (`id`, `term`, `mean`, `source`) VALUES
(1, 'Hukum Adat', 'Menurut kesimpulan hasil “Seminar Hukum Adat dan Pembangunan Hukum Nasional” diartikan sebagai hukum Indonesia asli yang tidak tertulis dalam bentuk perundang-undangan Republik Indonesia yang mengandung unsur-unsur agama', 'Rosdalina, 2017'),
(2, 'Teori Kenyataan', 'Teori yang menyatakan bahwa hukum adat terbentuk karena norma-norma yang berlaku memiliki sanksi dan pada kenyataannya masih ditaati dan dipatuhi oleh masyarakat secara sadar dan sepenuh hati', 'Mansur, 2018'),
(3, 'Teori Keputusan', 'Teori yang menyatakan hukum adat terbentuk dari adat istiadat yang digunakan oleh fungsionaris hukum (kepala adat) untuk memutus sengketa yang kemudian diikuti oleh fungsionaris hukum lainnya.', '-'),
(4, 'Teori Receptio In Complexu', 'Teori yang menyatakan bahwa hukum adat merupakan hukum agama.', '-'),
(10, 'Teori Receptie', 'Teori yang menyatakan hukum adat berbeda dengan hukum agama. Namun hukum agama terutama Islam diterima masyarakat selama dikehendaki oleh hukum adat.', ''),
(11, 'Teori Receptio A Contrario', 'Teori yang menyatakan hukum adat adalah hukum yang berbeda dengan hukum Islam dan tidak bisa dicampuradukkan.', ''),
(12, 'Teori Penetration', 'Teori yang menyatakan bahwa hukum Islam masuk ke Indonesia dan mengakar dalam kehidupan penduduk Indonesia dan membawa pengaruh normatif dalam kebudayaan.', ''),
(13, 'Teori Sinkritisme', 'Teori yang menyatakan bahwa hukum adat merupakan sinkretisme/ perpaduan dengan hukum Islam.', ''),
(14, 'Teori Zat', 'Teori yang menyatakan hukum adat yang dijalankan adalah yang tidak bertentangan dengan hukum Islam.', ''),
(15, 'Monodualisme', 'Sifat hukum adat yang statis sekaligus dinamis. Statis karena hukum adat itu bertujuan membangun keteraturan. Dinamis karena mengikuti perkembangan masyarakat.', ''),
(16, 'Plastis/ Elastis', 'Sifat hukum adat yang dapat menyesuaikan diri dengan kebutuhan masyarakat.', ''),
(17, 'Komunal', 'Salah satu sifat hukum adat yang berarti hukum adat bersifat kebersamaan atau mementingkan kepentingan bersama di atas kepentingan pribadi.', ''),
(18, 'Kontan', 'Salah satu sifat hukum adat yang berarti hukum adat dilakukan serta merta atau diselesaikan saat itu juga.', ''),
(19, 'Magis Religius', 'Salah satu sifat hukum adat yang berarti hukum adat erat kaitannya dengan kepercayaan pada hal-hal gaib.', ''),
(20, 'Konkret', 'Salah satu sifat hukum adat yang berarti nyata, jelas, tidak diam-diam, tidak sembunyi-sembunyi, atau tidak samar-samar, diketahui dan didengar orang lain dan nampak terjadi ijab-kabul (serah-terima).', ''),
(21, 'Tradisional', 'Salah satu corak hukum adat di mana Hukum adat diwariskan secara turun temurun sejak dahulu hingga sekarang tetap dipakai dan dihormati (Mansur, 2018; Rosdalina, 2017; Wiranata, 2005; Pidie, 2014).', ''),
(22, 'Masyarakat Hukum Adat', 'Komunitas (paguyuban) sosial manusia yang merasa bersatu karena terikat oleh kesamaan leluhur dan atau wilayah tertentu, memiliki kekayaan sendiri, dipimpin oleh seseorang atau beberapa orang yang dipandang memiliki kewibawaan atau kekuasaan, memiliki tata nilai sebagai pedoman hidup, serta tidak mempunyai keinginan untuk memisahkan diri (Rosdalina, 2017).', ''),
(23, 'Geneologis', 'Masyarakat adat yang anggota-anggotanya merasa terikat dalam suatu ketertiban berdasarkan pada kepercayaan bahwa mereka semua berasal dari satu keturunan yang sama.', ''),
(24, 'Patrilineal', 'Susunan masyarakat yang menarik garis keturunan melalui garis keturunan bapak/ laki-laki. Contoh pada masyarakat Gayo, Batak, Bali, Ambon.', ''),
(25, 'Matrilineal', 'Susunan masyarakat yang menarik garis keturunan melalui garis keturunan ibu/ perempuan. Contoh pada masyarakat Minangkabau dan Kerinci.', ''),
(26, 'Patriakal', 'Struktur sosial yang meletakkan kedudukan laki-laki lebih di atas perempuan dan memiliki kekuasaan atas perempuan.', ''),
(27, 'Matriakal', 'Dominasi kepemimpinan perempuan dalam beberapa aspek kehidupan termasuk garis keturunan.', ''),
(28, 'Endogami', 'Bentuk perkawinan dalam satu klan yang sama.', ''),
(29, 'Eksogami', 'Bentuk perkawinan antar anggota suatu klan dengan anggota klan lain di luar klannya.', ''),
(30, 'Parental', 'Sistem kekerabatan yang menarik garis keturunan dari kedua orang tua.', ''),
(31, 'Bilateral', 'Hubungan kekerabatan yang menghendaki perlakuan sama antara kerabat dari ayah maupun dari ibu.', ''),
(32, 'Patrilineal Beralih-alih', 'Susunan masyarakat yang menarik garis keturunan secara bergantian tergantung pada bentuk perkawinan yang dilakukan oleh orang tuanya, yaitu apakah melaui kawin jujur, kawin semendo, ataupun kawin semendo rajo-rajo. Contoh pada masyarakat Rejang Lebong dan Lampung Papadon.', ''),
(33, 'Teritorial', 'Masyarakat adat yang anggota-anggotanya merasa terikat dalam suatu ketertiban berdasarkan pada daerah atau tanah kelahirannya, yang didiami oleh orangtuanya, neneknya, nenek moyangnya secara turun temurun.', ''),
(34, 'Masyarakat Hukum Desa', 'Segolongan atau sekumpulan orang yang hidup bersama berasaskan pandangan hidup, cara hidup dan sistem kepercayaan yang sama, mendiami daerah yang sama, merupakan satu kesatuan tata susunan tertentu baik keluar maupun ke dalam.', ''),
(35, 'Persekutuan Desa', 'Kesatuan yang melingkupi beberapa masyarakat hukum desa yang masing-masingnya tetap merupakan kesatuan-kesatuan yang berdiri sendiri. Contoh, Kuria di Angkola dan Mandailing sebagai masyarakat hukum wilayah melingkupi beberapa Huta.', ''),
(36, 'Kuria', 'Kesatuan masyarakat hukum adat Batak yang terdiri dari beberapa Huta (Kampung).', ''),
(37, 'Huta', 'Kampung dalam Bahasa Batak yang didirikan oleh suatu marga tertentu, seperti Huta Siallangan yang berarti Huta/ Kampung yang didirikan oleh marga Siallangan.', ''),
(38, 'Marga', 'Istilah dalam masyarakat Batak yang merujuk pada nama klan atau kelompok masyarakat Batak.', ''),
(39, 'Perserikatan Desa', 'Satu kesatuan sosial territorial yang didasarkan pada kerjasama di berbagai lapangan demi kepentingan bersama masyakat hukum desa yang tergabung dalam masyarakat hukum serikat desa.', ''),
(40, 'Geneologis-Teritorial', 'Masyarakat adat yang anggota-anggotanya merasa tidak terikat hanya pada kesamaan tempat tinggal, tetapi juga terdapat ikatan darah atau keturunan yang sama. Contohnya di pedalaman Kalimantan, Irian dan Sumatera.', ''),
(41, 'Persekutuan Hukum Adat', 'Pengertian yang lebih sempit dari masyarakat adat. Sub kelompok masyarakat adat yang didasarkan pada kekerabatan, ketetanggaan atau keorganisasian.', ''),
(42, 'Persekutuan Kekerabatan', 'Bentuk-benuk hubungan kekerabatan yang terjadi karena ikatan darah (geneologis) berdasarkan keturunan melalui garis bapak (patrilineal), garis ibu (matrilineal) maupun orang tua (bilateral/ parental).', ''),
(43, 'Persekutuan Ketetanggaan', 'Hubungan bersebrangan rumah yang ikatanya didasarkan pada rasa kekeluargaan karena menempati suatu wilayah seperti pedukuhan atau desa.', ''),
(44, 'Persekutuan Organisasi', 'Para anggotanya terikat dalam rasa kekeluargaan karena terhimpun dalam satu kesatuan organisasi.', ''),
(45, 'Safiatudin', 'Wanita Aceh yang menjadi pahlawan pemberani dan menjadi ratu Aceh.', ''),
(46, 'Aneuk Jamee', 'Hukum waris yang berlaku pada masyarakat Aceh.', ''),
(47, 'Harta Pusaka Rendah', 'Harta yang dapat diwariskan baik kepada anak perempuan maupun anak laki-laki secara individual.', ''),
(48, 'Harta Pusaka Tinggi', 'Harta yang hanya diwariskan pada anak perempuan (masyarakat matrilineal). Biasanya terdapat pada sistem waris kolektif.', ''),
(49, 'Cahrot', 'Tahap pencarian jodoh dalam sistem perkawinanan masyarakat adat Aceh', ''),
(50, 'Meulake', 'Tahap melamar dalam sistem perkawinanan masyarakat adat Aceh', ''),
(51, 'Seulangke', 'Orang yang dirasa bijak dalam berbicara yang dikirim oleh keluarga yang sedang melakukan pencarian jodoh untuk anaknya.', ''),
(52, 'Ba Ranup', 'Upacara membawa sirih sebagai penguat ikatan yang dilaksanakan pada tahap melamar (Meulake) dalam sistem perkawinanan masyarakat adat Aceh', ''),
(53, 'Peukong Haba', 'Istilah yang digunakan oleh masyarakat adat Aceh untuk menyebut tahap pembicaraan mengenai kapan hari perkawinan akan dilangsungkan.', ''),
(54, 'Jeulamee', 'Uang mahar yang akan diterima oleh calom mempelai perempuan pada masyarakat adat Aceh.', ''),
(55, 'Jak Ba Tanda', 'Tahap pertunangan atau pergi membawa tanda seperti cicin sebagai pertanda sudah dipinang pada masyarakat adat Aceh.', ''),
(56, 'Meugaca', 'Bagian dari rangkaian proses perkawinan pada masyarakat adat Aceh yang dilaksanakan selama tiga hari tiga malam berupa memakai inai bagi pengantin laki-laki maupun perempuan.', ''),
(57, 'Boh Gaca', 'Istilah lain dari upacara Meugaca', ''),
(58, 'Ijab Kabul', 'Proses perkawinan berdasarkan syariat Islam.', ''),
(59, 'Tueng Linto Baroe', 'Proses perkawinan pada masyarakat Aceh yang berarti menerima pengantin pria oleh pihak perempuan berdasarkan tradisi Aceh.', ''),
(60, 'Tueng Dara Baroe', 'Proses perkawinan pada masyarakat Aceh yang berarti penjemputan pengantin perempuan.', ''),
(61, 'Peng angoh', 'Uang hangus (pada masyarakat Aceh) atau uang yang diberikan oleh pihak laki-laki untuk membantu pihak perempuan dalam menyelenggarakan pesta perkawinan dan membeli isi kamar.', ''),
(62, 'Idang', 'Hidangan yang diberikan dari pihak pengantin laki-laki kepada pihak perempuan pada masyarakat adat Aceh.', ''),
(63, 'Peunuwo', 'Pemulang atau hidangan yang diberikan oleh pihak perempuan pada saat intat dara baro kepada pihak laki-laki sebanyak talam dengan jumlah ganjil pada masyarakat adat Aceh.', ''),
(64, 'Intat Linto Baro', 'Mengantar pengantin pria pada masyarakat adat Aceh.', ''),
(65, 'Intat Daro Baro', 'Mengantar pengantin wanita pada masyarakat adat Aceh.', ''),
(66, 'Mukim', 'Kesatuan masyarakat (hukum Adat dalam wilayah Aceh yang terdiri beberapa Gampong yang mempunyai batas-batas wilayah tertentu.', ''),
(67, 'Imam Mukim', 'Kepala Mukim dan Pemangku Adat di Kemukiman masyarakat Aceh.', ''),
(68, 'Tuha Papan', 'Suatu Badan Kelengkapan Gampong dan Mukim yang terdiri dari unsur Pemerintah, unsur Agama, unsur pimpinan Adat, pemuka masyarakat, unsur cerdik pandai unsur pemuda/wanita dan unsur kelompok organisasi masyarakat Aceh.', ''),
(69, 'Gampong', 'Suatu wilayah yang ditempati oleh sejumlah penduduk Aceh sebagai kesatuan masyarakat yang terendah dan berhak menyelenggarakan rumah tangganya sendiri.', ''),
(70, 'Geuchik', 'Orang yang dipilih dan dipercaya oleh masyarakat serta diangkat oleh Pemerintah Daerah Kabupaten Kota untuk memimpin Pemerintahan Gampong di Aceh.', ''),
(71, 'Tuha Peut', 'Suatu badan kelengkapan Gampong dan Mukim di Aceh yang terdiri dari unsur Pemerintahan, unsur Agama, unsur Pimpinan Adat, unsur cerdik pandai yang berada di Gampong dan Mukim yang berfungsi memberi nasehat kepada Keuchik/Mukim.', ''),
(72, 'Imam Meunasah', 'Orang yang memimpin kegiatan-kegiatan masyarakat di Gampong yang berkaitan dengan Bidang Agama Islam dan pelaksanaan syari’at Islam di Aceh.', ''),
(73, 'Kejruen Blang', 'Orang yang membantu Geuchik di bidang pengaturan dan penggunaan irigasi untuk persawahan di Aceh.', ''),
(74, 'Panglima Laot', 'Orang yang memimpin Adat Istiadat, kebiasaan-kebiasaan yang berlaku dalam penangkapan ikan di laut, termasuk pengatur tempat/areal penangkapan ikan dan penyelesaian sengketa di Aceh.', ''),
(75, 'Peutua Seubeubok', 'Orang yang memimpin dan mengatur ketentuan-ketentuan tentang pembukaan penggunaan lahan untuk perlengkapan perkebunan di Aceh.', ''),
(76, 'Haria Peukan', 'Orang yang mengatur ketertiban, keamanan dan kebersihan pasar serta mengutip retribusi pasar Gampong di Aceh.', ''),
(77, 'Syahbandar', 'Orang yang memimpin mengatur hambatan kapal/perahu lalu lintas keluar dan masuk kapal/perahu di bidang angkutan laut, sungai dan danau di Aceh.', ''),
(78, 'Juelen', 'Bentuk perkawinan pada masyarakat Tanah Gayo yang diibaratkan menjual pengantin pria kepada suaminya dan bukan lagi menjadi milik orang tuanya sehingga tidak lagi bergaul dengan orang tuanya jika mahar sudah lunas.', ''),
(79, 'Angkap', 'Bentuk perkawinan pada masyarakat Tanah Gayo yang mengakibatkan suami tinggal di tempat istri karena suami tidak bisa melunasi maharnya.', ''),
(80, 'Teniron', 'Permintaan orang tua calon istri secara adat pada masyarakat Tanah Gayo.', ''),
(81, 'Angkap Duduk Edet', 'Bentuk perkawinan pada masyarakat Tanah Gayo yang mengakibatkan suami tinggal/ mengikuti istri selama mahar istri belum dilunasi. Jika mahar sudah dilunasi, maka suami berhak membawa istri dan anaknya ke kampungnya.', ''),
(82, 'Angkap Sentaran', 'Bentuk perkawinan pada masyarakat Tanah Gayo yang mengakibatkan suami tinggal/ mengikuti istri sampai batas waktu yang telah disepakati karena adanya urusan istri yang harus diselesaikan seperti orang tua istri sudah sangat ozor/tua, sehingga masih memerlukan perawatan. Setelah orang tuanya meninggal maka mereka boleh pindah ke kampung suaminya.', ''),
(83, 'Angkap Empat Mas', 'Bentuk perkawinan pada masyarakat Tanah Gayo yang mengakibatkan suami tinggal/ mengikuti istri untuk selama-lamanya.', ''),
(84, 'Naik', 'Bentuk kawin lari pada masyarakat Tanah Gayo yang terjadi karena seorang pemuda melarikan seorang gadis untuk di jadikan istrinya, atau seorang gadis yang menyerahkan dirinya pada seorang pemuda untuk dijadikan teman hidupnya.', ''),
(85, 'Angkap Nasab', 'Istilah lain dari Angkap Empat Mas.', ''),
(86, 'Mah Tabak', 'Bentuk perkawinan pada masyarakat Tanah Gayo ketika seorang pemuda yang langsung menghadap orang tua gadis dengan permintaan untuk dikawinkan dengan anak gadisnya. Menurut pertimbangan laki-laki tersebut bila melalui prosedur biasa dia tidak akan mendapat perempuan yang diinginkannya itu.', ''),
(87, 'Ngalih', 'Bentuk perkawinan pada masyarakat Tanah Gayo yang terjadi karena meninggalnya salah satu pihak suami atau isteri. Apabila suami meninggal, maka istri atau janda tersebut ‘diambil alih’ oleh saudara suami yang meninggal, atau sebaliknya bila istri yang meninggal, maka suami mengambil saudara istri sebagai ganti istrinya yang telah meninggal tersebut.', ''),
(88, 'Berkeroa', 'Bentuk perkawinan lebih dari satu orang istri (poligami) pada masyarakat Tanah Gayo yang berdasarkan pada ketentuan syari’at Islam.', ''),
(89, 'Kuso-Kini', 'Bentuk perkawinan pada masyarakat Tanah Gaoy yang memberikan kebebasan kepada suami-istri untuk memilih tempat menetap.', ''),
(90, 'Sumang', 'Hukum pidana yang berlaku pada masyarakat Tanah Gayo', ''),
(91, 'Sumang Percarekan', 'Hukum pidana yang berlaku pada masyarakat Tanah Gayo berkaitan degan perkataan atau pembicaraan antara pria dan wanita yang tidak pantas.', ''),
(92, 'Sumang Kenunulen', 'Hukum pidana yang berlaku pada masyarakat Tanah Gayo berkaitan dengan kedudukan atau perbuatan duduk di tempat yang menimbulkan kecurigaan untuk beebuat tidak baik.', ''),
(93, 'Sumang Pelangkahen', 'Hukum pidana yang berlaku pada masyarakat Tanah Gayo berkaitan dengan perjalanan yaitu berjalan tanpa muhrim.', ''),
(94, 'Sumang Penengonen', 'Hukum pidana yang berlaku pada masyarakat Tanah Gayo berkaitan dengan perbuatan melihat pria/ wanita dengan rasa syahwat.', ''),
(95, 'Pematang', 'Jenis harta warisan pada masyarakat Tanah Gayo yang ditinggalkan oleh suami untuk perawatan istrinya dan menjamin agar istrinya tersebut tidak terlantar atau ditelantarkan oleh anak-anaknya.', ''),
(96, 'Patah Titi', 'Sistem waris masyarakat Tanah Gayo ketika seorang cucu tidak berhak mewarisi harta kakek-neneknya jika orang tuanya telah meninggal terlebih dahulu sebelum kakek-neneknya.', ''),
(97, 'Sarak Opat', 'Sistem pemerintahan yang dianut oleh masyarakat Tanah Gayo.', ''),
(98, 'Reje', 'Bagian dari Sarak Opat yang memegang kekuasaan adat pada masyarakat Tanah Gayo dalam batas-batas daerah tertentu.', ''),
(99, 'Petue', 'Bagian dari Sarak Opat yang menjadi pendamping reje dalam melaksanakan tugasnya.', ''),
(100, 'Imem', 'Bagian dari Sarak Opat sebgai seorang ahli agama Islam.', ''),
(101, 'Saudere', 'Bagian dari Sarak Opat yang berarti rakyat atau masyarakat Gayo.', ''),
(102, 'Kawin Jujur', 'Bentuk perkawinan pada masyarakat patrilineal terutama masyarakt Batak di mana pihak laki-laki melakukan pembayaran uang jujur. Istilah lainnya boli, tuhor, parunjuk, pangolin.', ''),
(103, 'Pantang Cerai', 'Aturan perkawinan pada masyarakat Batak di mana sepasang suami-istri harus menjalani susah senang kehidupan Bersama dan istri berada di bawah kekuasaan kerabat suami.', ''),
(104, 'Leviraat', 'Sistem perkawinan di mana seorang laki-laki menikahi saudara istrinya ketika istrinya telah meninggal atau seorang laki-laki menikahi istri saudaranya yang telah meninggal.', ''),
(105, 'Paraekhon', 'Istilah perkawinan levirate pada masyarakat Batak Toba.', ''),
(106, 'Mangabia', 'Istilah lain dari paraekhon pada masyarakat Batak Toba.', ''),
(107, 'Lakoman', 'Istilah perkawinan levirate pada masyarakat Batak Karo.', ''),
(108, 'Mangariksa', 'Rangkaian perkawinan Batak berupa kunjungan dari pihak mempelai laki-laki kepada pihak perempuan.', ''),
(109, 'Pabangkit Harta', 'Rangkaian perkawinan Batak berupa lamaran.', ''),
(110, 'Marhori-hori Dinding', 'Rangkaian perkawinan Batak berupa pembicaraaan mengenai rencana perkawinan serta pestanya.', ''),
(111, 'Patua Hata', 'Rangkaian perkawinan Batak di mana para orang tua memberikan petuha atau nasihat ssebagai bekal kepada kedua mempelai.', ''),
(112, 'Marhata Sinamot', 'Rangkaian perkawinan Batak di mana pihak pria mendatangi pihak wanita untuk membicarakan perundingan yang menyangkut tuhor/ tukur, bare-bere/upa tulang, perkempun, perbibin, perkembaren, ulu emas.', ''),
(113, 'Opung Doli', 'Sebutan kakek pada masyarakat Batak.', ''),
(114, 'Opung Boru', 'Sebutan nenek pada masyarakat Batak.', ''),
